INSERT INTO course_genie.syllabus (syllabus_id, content, section_id) VALUES (3, '<p><b>Course Code:</b> GCIS-124<br><b>Section Code:</b> GCIS-124-600<br><b>Course Name:</b> SOFTWARE DEVELOPMENT AND PROBLEM SOLVING II<br><b>Description:</b> null<br><b>Credits:</b> 3<br><b>Semester:</b> Spring 2025<br><b>Professor:</b> Danilo Kovacevic<br><br><b>Assessments:</b><br>- Quiz (QZ)<br>  <b>Max Points:</b> 20<br>  <b>CLOs:</b><br>    - GCIS-124-1: Describe the basic abstractions and constructs of a selected contemporary, object-oriented programming language.<br><br>- PE (PE)<br>  <b>Max Points:</b> 40<br>  <b>CLOs:</b><br>    - GCIS-124-2: Describe and apply problem-solving skills that are appropriate to solve a variety of computing problems of varying degrees of difficulty and complexity.<br><br>- Midterm Exam  (ME)<br>  <b>Max Points:</b> 20<br>  <b>CLOs:</b><br>    - GCIS-124-1: Describe the basic abstractions and constructs of a selected contemporary, object-oriented programming language.<br>    - GCIS-124-2: Describe and apply problem-solving skills that are appropriate to solve a variety of computing problems of varying degrees of difficulty and complexity.<br><br>- Final Exam  (FE)<br>  <b>Max Points:</b> 20<br>  <b>CLOs:</b><br>    - GCIS-124-3: Describe and apply a variety of algorithms and data structures to implement solutions to a variety of computing problems of varying degrees of difficulty and complexity.<br>    - GCIS-124-4: Apply fundamentals of software engineering including software design, testing, and incremental development.<br><br></p>', 15);
INSERT INTO course_genie.syllabus (syllabus_id, content, section_id) VALUES (4, '<p><b>Course Code:</b> NSSA-241<br><b>Section Code:</b> NSSA-241-601<br><b>Course Name:</b> INTRODUCTION TO ROUTING AND SWITCHING<br><b>Description:</b> null<br><b>Credits:</b> 3<br><b>Semester:</b> Spring 2025<br><b>Professor:</b> Huda Saadeh<br><br><b>Assessments:</b><br>- Quiz (QZ1)<br>  <b>Max Points:</b> 5<br>  <b>CLOs:</b><br>    - NSSA-241-1: Locate, synthesize, and discuss current RFCs, IEEE standards, and other related standards, as well as describe the standards bodies and the standardization process as they apply to local area networking.<br><br>- Quiz (QZ2)<br>  <b>Max Points:</b> 5<br>  <b>CLOs:</b><br>    - NSSA-241-2: Compare and contrast the OSI and TCP/IP models as they apply to contemporary communication protocols with a focus on layers 1, 2, and 3.<br><br>- Quiz (QZ3)<br>  <b>Max Points:</b> 5<br>  <b>CLOs:</b><br>    - NSSA-241-2: Compare and contrast the OSI and TCP/IP models as they apply to contemporary communication protocols with a focus on layers 1, 2, and 3.<br>    - NSSA-241-1: Locate, synthesize, and discuss current RFCs, IEEE standards, and other related standards, as well as describe the standards bodies and the standardization process as they apply to local area networking.<br><br>- Quiz (QZ4)<br>  <b>Max Points:</b> 5<br>  <b>CLOs:</b><br>    - NSSA-241-2: Compare and contrast the OSI and TCP/IP models as they apply to contemporary communication protocols with a focus on layers 1, 2, and 3.<br>    - NSSA-241-3: Explain the specifications of contemporary data network protocols.<br><br>- Practical Exam (PE1)<br>  <b>Max Points:</b> 5<br>  <b>CLOs:</b><br>    - NSSA-241-1: Locate, synthesize, and discuss current RFCs, IEEE standards, and other related standards, as well as describe the standards bodies and the standardization process as they apply to local area networking.<br><br>- Practical Exam (PE2)<br>  <b>Max Points:</b> 5<br>  <b>CLOs:</b><br>    - NS